internal sealed class SpecializedInt64KeyDictionary<TValue> : IReadOnlyDictionary<long, TValue>
{
    #region Constants
    private static readonly Func<TValue, TValue> PassThrough = static x => x;
    #endregion


    #region Fields
    private Entry[] _buckets;
    private int _size;
    private readonly float _loadFactor;
    #endregion


    #region Constructors
    /// <summary>
    /// Creates instance.
    /// </summary>
    /// <param name="bucketSize"></param>
    /// <param name="loadFactor"></param>
    private SpecializedInt64KeyDictionary(int bucketSize, float loadFactor)
    {
        this._buckets = (bucketSize == 0) ? Array.Empty<Entry>() : new Entry[bucketSize];
        this._loadFactor = loadFactor;
    }
    #endregion


    #region Create
    /// <summary>
    /// Creates a <see cref="SpecializedInt64KeyDictionary{TValue}"/> from an <see cref="IEnumerable{T}"/> according to a specified key selector function.
    /// </summary>
    /// <param name="source"></param>
    /// <param name="keySelector"></param>
    /// <returns></returns>
    public static SpecializedInt64KeyDictionary<TValue> Create(IEnumerable<TValue> source, Func<TValue, long> keySelector)
        => Create(source, keySelector, PassThrough);


    /// <summary>
    ///  Creates a <see cref="SpecializedInt64KeyDictionary{TValue}"/> from an <see cref="IEnumerable{T}"/> according to specified key selector and value selector functions.
    /// </summary>
    /// <typeparam name="TSource"></typeparam>
    /// <param name="source"></param>
    /// <param name="keySelector"></param>
    /// <param name="valueSelector"></param>
    /// <returns></returns>
    public static SpecializedInt64KeyDictionary<TValue> Create<TSource>(IEnumerable<TSource> source, Func<TSource, long> keySelector, Func<TSource, TValue> valueSelector)
    {
        const int initialSize = 4;
        const float loadFactor = 0.75f;
        var size = source.TryGetNonEnumeratedCount(out var count) ? count : initialSize;
        var bucketSize = CalculateCapacity(size, loadFactor);
        var result = new SpecializedInt64KeyDictionary<TValue>(bucketSize, loadFactor);

        foreach (var x in source)
        {
            var key = keySelector(x);
            var value = valueSelector(x);
            if (!result.TryAddInternal(key, value, out _))
                throw new ArgumentException($"Key was already exists. Key:{key}");
        }

        return result;
    }
    #endregion


    #region Add
    /// <summary>
    /// Add element.
    /// </summary>
    /// <param name="key"></param>
    /// <param name="value"></param>
    /// <param name="resultingValue"></param>
    /// <returns></returns>
    private bool TryAddInternal(long key, TValue value, out TValue resultingValue)
    {
        var nextCapacity = CalculateCapacity(this._size + 1, this._loadFactor);
        if (this._buckets.Length < nextCapacity)
        {
            //--- rehash
            var nextBucket = new Entry[nextCapacity];
            for (int i = 0; i < this._buckets.Length; i++)
            {
                var e = this._buckets[i];
                while (e is not null)
                {
                    var newEntry = new Entry(e.Key, e.Value, e.Hash);
                    AddToBuckets(nextBucket, key, newEntry, default, out _);
                    e = e.Next;
                }
            }

            var success = AddToBuckets(nextBucket, key, null, value, out resultingValue);
            this._buckets = nextBucket;
            if (success)
                this._size++;

            return success;
        }
        else
        {
            var success = AddToBuckets(this._buckets, key, null, value, out resultingValue);
            if (success)
                this._size++;

            return success;
        }

        #region Local Functions
        //--- please pass 'key + newEntry' or 'key + value'.
        static bool AddToBuckets(Entry[] buckets, long newKey, Entry newEntry, TValue value, out TValue resultingValue)
        {
            var hash = newEntry?.Hash ?? newKey.GetHashCode();
            var index = hash & (buckets.Length - 1);
            if (buckets[index] is null)
            {
                if (newEntry is null)
                {
                    resultingValue = value;
                    buckets[index] = new Entry(newKey, resultingValue, hash);
                }
                else
                {
                    resultingValue = newEntry.Value;
                    buckets[index] = newEntry;
                }
            }
            else
            {
                var lastEntry = buckets[index];
                while (true)
                {
                    if (lastEntry.Key == newKey)
                    {
                        resultingValue = lastEntry.Value;
                        return false;
                    }

                    if (lastEntry.Next is null)
                    {
                        if (newEntry is null)
                        {
                            resultingValue = value;
                            lastEntry.Next = new Entry(newKey, resultingValue, hash);
                        }
                        else
                        {
                            resultingValue = newEntry.Value;
                            lastEntry.Next = newEntry;
                        }
                        break;
                    }

                    lastEntry = lastEntry.Next;
                }
            }
            return true;
        }
        #endregion
    }


    /// <summary>
    /// Calculates bucket capacity.
    /// </summary>
    /// <param name="collectionSize"></param>
    /// <param name="loadFactor"></param>
    /// <returns></returns>
    private static int CalculateCapacity(int collectionSize, float loadFactor)
    {
        var initialCapacity = (int)(collectionSize / loadFactor);
        var capacity = 1;
        while (capacity < initialCapacity)
            capacity <<= 1;

        if (capacity < 8)
            return 8;

        return capacity;
    }
    #endregion


    #region Get
    /// <summary>
    /// Gets the element that has the specified key in the read-only dictionary or the default value of the element type.
    /// </summary>
    /// <param name="key"></param>
    /// <param name="defaultValue"></param>
    /// <returns></returns>
    public TValue GetValueOrDefault(long key, TValue defaultValue = default)
        => this.TryGetValue(key, out var value)
        ? value
        : defaultValue;
    #endregion


    #region IReadOnlyDictionary<TKey, TValue> implementations
    /// <summary>
    /// Gets the element that has the specified key in the read-only dictionary.
    /// </summary>
    /// <param name="key">The key to locate.</param>
    /// <returns>The element that has the specified key in the read-only dictionary.</returns>
    public TValue this[long key]
        => this.TryGetValue(key, out var value)
        ? value
        : throw new KeyNotFoundException();


    /// <summary>
    /// Gets an enumerable collection that contains the keys in the read-only dictionary.
    /// </summary>
    public IEnumerable<long> Keys
        => throw new NotImplementedException();


    /// <summary>
    /// Gets an enumerable collection that contains the values in the read-only dictionary.
    /// </summary>
    public IEnumerable<TValue> Values
        => throw new NotImplementedException();


    /// <summary>
    /// Gets the number of elements in the collection.
    /// </summary>
    public int Count
        => this._size;


    /// <summary>
    /// Determines whether the read-only dictionary contains an element that has the specified key.
    /// </summary>
    /// <param name="key">The key to locate.</param>
    /// <returns>
    /// true if the read-only dictionary contains an element that has the specified key; otherwise, false.
    /// </returns>
    public bool ContainsKey(long key)
        => this.TryGetValue(key, out _);


    /// <summary>
    /// Gets the value that is associated with the specified key.
    /// </summary>
    /// <param name="key">The key to locate.</param>
    /// <param name="value">
    /// When this method returns, the value associated with the specified key, if the key is found;
    /// otherwise, the default value for the type of the value parameter.
    /// This parameter is passed uninitialized.
    /// </param>
    /// <returns>true if the object that implements the <see cref="IReadOnlyDictionary{TKey, TValue}"/> interface contains an element that has the specified key; otherwise, false.</returns>
    [MethodImpl(MethodImplOptions.AggressiveInlining)]
    public bool TryGetValue(long key, out TValue value)
    {
        var hash = key.GetHashCode();
        var index = hash & (this._buckets.Length - 1);
        var next = this._buckets[index];
        while (next is not null)
        {
            if (next.Key == key)
            {
                value = next.Value;
                return true;
            }
            next = next.Next;
        }
        value = default;
        return false;
    }


    /// <summary>
    /// Returns an enumerator that iterates through the collection.
    /// </summary>
    /// <returns>An enumerator that can be used to iterate through the collection.</returns>
    public IEnumerator<KeyValuePair<long, TValue>> GetEnumerator()
        => throw new NotImplementedException();


    /// <summary>
    /// Returns an enumerator that iterates through a collection.
    /// </summary>
    /// <returns>An <see cref="IEnumerator"/> object that can be used to iterate through the collection.</returns>
    IEnumerator IEnumerable.GetEnumerator()
        => throw new NotImplementedException();
    #endregion


    #region Inner Classes
    /// <summary>
    /// Represents <see cref="SpecializedInt64KeyDictionary{TValue}"/> entry.
    /// </summary>
    private class Entry
    {
        public readonly long Key;
        public readonly TValue Value;
        public readonly int Hash;
        public Entry Next;

        public Entry(long key, TValue value, int hash)
        {
            this.Key = key;
            this.Value = value;
            this.Hash = hash;
        }
    }
    #endregion
}



